
<map>
  <node ID="root" TEXT="小样本学习">
    <node TEXT="小样本学习方法按照对数据和任务处理的方法不同可分为：基于数据增强、基于迁移学习、基于元学习、基于度量学习和多模态学习" ID="FC2ReLVGKa" _mubu_text="%3Cspan%3E%E5%B0%8F%E6%A0%B7%E6%9C%AC%E5%AD%A6%E4%B9%A0%E6%96%B9%E6%B3%95%E6%8C%89%E7%85%A7%E5%AF%B9%E6%95%B0%E6%8D%AE%E5%92%8C%E4%BB%BB%E5%8A%A1%E5%A4%84%E7%90%86%E7%9A%84%E6%96%B9%E6%B3%95%E4%B8%8D%E5%90%8C%E5%8F%AF%E5%88%86%E4%B8%BA%EF%BC%9A%E5%9F%BA%E4%BA%8E%E6%95%B0%E6%8D%AE%E5%A2%9E%E5%BC%BA%E3%80%81%E5%9F%BA%E4%BA%8E%E8%BF%81%E7%A7%BB%E5%AD%A6%E4%B9%A0%E3%80%81%E5%9F%BA%E4%BA%8E%E5%85%83%E5%AD%A6%E4%B9%A0%E3%80%81%E5%9F%BA%E4%BA%8E%E5%BA%A6%E9%87%8F%E5%AD%A6%E4%B9%A0%E5%92%8C%E5%A4%9A%E6%A8%A1%E6%80%81%E5%AD%A6%E4%B9%A0%3C/span%3E" STYLE="bubble" POSITION="right"/>
    <node TEXT="基于数据增强：" ID="YkAYWObuB2" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E6%95%B0%E6%8D%AE%E5%A2%9E%E5%BC%BA%EF%BC%9A%3C/span%3E" STYLE="bubble" POSITION="right">
      <node TEXT="基于数据增强的方法主要从数据本身出发，扩充带标签的数据集。" ID="xn2yZAnWbm" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E6%95%B0%E6%8D%AE%E5%A2%9E%E5%BC%BA%E7%9A%84%E6%96%B9%E6%B3%95%E4%B8%BB%E8%A6%81%E4%BB%8E%E6%95%B0%E6%8D%AE%E6%9C%AC%E8%BA%AB%E5%87%BA%E5%8F%91%EF%BC%8C%E6%89%A9%E5%85%85%E5%B8%A6%E6%A0%87%E7%AD%BE%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="手工数据增强：" ID="UkXJ8yiOjL" _mubu_text="%3Cspan%3E%E6%89%8B%E5%B7%A5%E6%95%B0%E6%8D%AE%E5%A2%9E%E5%BC%BA%EF%BC%9A%3C/span%3E" STYLE="fork">
        <node TEXT="数据变换：" ID="h7OJYL5NUa" _mubu_text="%3Cspan%3E%E6%95%B0%E6%8D%AE%E5%8F%98%E6%8D%A2%EF%BC%9A%3C/span%3E" STYLE="fork">
          <node TEXT="对图像进行裁剪、旋转、翻转、滤波和噪点处理，或者对像素点进行操作，删除、替换、生成等。" ID="7JZXjESIQv" _mubu_text="%3Cspan%3E%E5%AF%B9%E5%9B%BE%E5%83%8F%E8%BF%9B%E8%A1%8C%E8%A3%81%E5%89%AA%E3%80%81%E6%97%8B%E8%BD%AC%E3%80%81%E7%BF%BB%E8%BD%AC%E3%80%81%E6%BB%A4%E6%B3%A2%E5%92%8C%E5%99%AA%E7%82%B9%E5%A4%84%E7%90%86%EF%BC%8C%E6%88%96%E8%80%85%E5%AF%B9%E5%83%8F%E7%B4%A0%E7%82%B9%E8%BF%9B%E8%A1%8C%E6%93%8D%E4%BD%9C%EF%BC%8C%E5%88%A0%E9%99%A4%E3%80%81%E6%9B%BF%E6%8D%A2%E3%80%81%E7%94%9F%E6%88%90%E7%AD%89%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="该方法实现简单，但无法增加样本的多样性，因此常用作为数据预处理" ID="1QBbHgHUT1" _mubu_text="%3Cspan%3E%E8%AF%A5%E6%96%B9%E6%B3%95%E5%AE%9E%E7%8E%B0%E7%AE%80%E5%8D%95%EF%BC%8C%E4%BD%86%E6%97%A0%E6%B3%95%E5%A2%9E%E5%8A%A0%E6%A0%B7%E6%9C%AC%E7%9A%84%E5%A4%9A%E6%A0%B7%E6%80%A7%EF%BC%8C%E5%9B%A0%E6%AD%A4%E5%B8%B8%E7%94%A8%E4%BD%9C%E4%B8%BA%E6%95%B0%E6%8D%AE%E9%A2%84%E5%A4%84%E7%90%86%3C/span%3E" STYLE="fork"/>
        </node>
        <node TEXT="特征增强：" ID="MLvOtPrdsZ" _mubu_text="%3Cspan%3E%E7%89%B9%E5%BE%81%E5%A2%9E%E5%BC%BA%EF%BC%9A%3C/span%3E" STYLE="fork">
          <node TEXT="将像素信息映射到高维度空间，找到更丰富的特征信息，使得每一类区别更明显，同时也得保证泛化性好" ID="EqmiIq9R3F" _mubu_text="%3Cspan%3E%E5%B0%86%E5%83%8F%E7%B4%A0%E4%BF%A1%E6%81%AF%E6%98%A0%E5%B0%84%E5%88%B0%E9%AB%98%E7%BB%B4%E5%BA%A6%E7%A9%BA%E9%97%B4%EF%BC%8C%E6%89%BE%E5%88%B0%E6%9B%B4%E4%B8%B0%E5%AF%8C%E7%9A%84%E7%89%B9%E5%BE%81%E4%BF%A1%E6%81%AF%EF%BC%8C%E4%BD%BF%E5%BE%97%E6%AF%8F%E4%B8%80%E7%B1%BB%E5%8C%BA%E5%88%AB%E6%9B%B4%E6%98%8E%E6%98%BE%EF%BC%8C%E5%90%8C%E6%97%B6%E4%B9%9F%E5%BE%97%E4%BF%9D%E8%AF%81%E6%B3%9B%E5%8C%96%E6%80%A7%E5%A5%BD%3C/span%3E" STYLE="fork"/>
          <node TEXT="方法可以是几个子空间信息叠加，或者修改特征提取网络。" ID="UwbZm3f2IU" _mubu_text="%3Cspan%3E%E6%96%B9%E6%B3%95%E5%8F%AF%E4%BB%A5%E6%98%AF%E5%87%A0%E4%B8%AA%E5%AD%90%E7%A9%BA%E9%97%B4%E4%BF%A1%E6%81%AF%E5%8F%A0%E5%8A%A0%EF%BC%8C%E6%88%96%E8%80%85%E4%BF%AE%E6%94%B9%E7%89%B9%E5%BE%81%E6%8F%90%E5%8F%96%E7%BD%91%E7%BB%9C%E3%80%82%3C/span%3E" STYLE="fork"/>
        </node>
        <node TEXT="模型建模：" ID="KUkkxbUEol" _mubu_text="%3Cspan%3E%E6%A8%A1%E5%9E%8B%E5%BB%BA%E6%A8%A1%EF%BC%9A%3C/span%3E" STYLE="fork">
          <node TEXT="利用计算模型模拟真实样本。" ID="nNg9T2chA3" _mubu_text="%3Cspan%3E%E5%88%A9%E7%94%A8%E8%AE%A1%E7%AE%97%E6%A8%A1%E5%9E%8B%E6%A8%A1%E6%8B%9F%E7%9C%9F%E5%AE%9E%E6%A0%B7%E6%9C%AC%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="该方法可以对样本进行高保真、低成本的增强，但生成样本与真实样本存在着域差距" ID="9K7MCl91HS" _mubu_text="%3Cspan%3E%E8%AF%A5%E6%96%B9%E6%B3%95%E5%8F%AF%E4%BB%A5%E5%AF%B9%E6%A0%B7%E6%9C%AC%E8%BF%9B%E8%A1%8C%E9%AB%98%E4%BF%9D%E7%9C%9F%E3%80%81%E4%BD%8E%E6%88%90%E6%9C%AC%E7%9A%84%E5%A2%9E%E5%BC%BA%EF%BC%8C%E4%BD%86%E7%94%9F%E6%88%90%E6%A0%B7%E6%9C%AC%E4%B8%8E%E7%9C%9F%E5%AE%9E%E6%A0%B7%E6%9C%AC%E5%AD%98%E5%9C%A8%E7%9D%80%E5%9F%9F%E5%B7%AE%E8%B7%9D%3C/span%3E" STYLE="fork"/>
        </node>
      </node>
      <node TEXT="自动增强：" ID="hd7aKrnSCi" _mubu_text="%3Cspan%3E%E8%87%AA%E5%8A%A8%E5%A2%9E%E5%BC%BA%EF%BC%9A%3C/span%3E" STYLE="fork">
        <node TEXT="主要是利用生成网络(GAN)学习源数据并生成具有同分布的数据，从而扩充数据集，" ID="ZSqq7exQkT" _mubu_text="%3Cspan%3E%E4%B8%BB%E8%A6%81%E6%98%AF%E5%88%A9%E7%94%A8%E7%94%9F%E6%88%90%E7%BD%91%E7%BB%9C(GAN)%E5%AD%A6%E4%B9%A0%E6%BA%90%E6%95%B0%E6%8D%AE%E5%B9%B6%E7%94%9F%E6%88%90%E5%85%B7%E6%9C%89%E5%90%8C%E5%88%86%E5%B8%83%E7%9A%84%E6%95%B0%E6%8D%AE%EF%BC%8C%E4%BB%8E%E8%80%8C%E6%89%A9%E5%85%85%E6%95%B0%E6%8D%AE%E9%9B%86%EF%BC%8C%3C/span%3E" STYLE="fork"/>
        <node TEXT="该方法可以自动的生成大量样本，无需人工干预，并且避免了域差距。但网络生成样本的保真性和稳定性仍需考虑" ID="JNh4ytpFdT" _mubu_text="%3Cspan%3E%E8%AF%A5%E6%96%B9%E6%B3%95%E5%8F%AF%E4%BB%A5%E8%87%AA%E5%8A%A8%E7%9A%84%E7%94%9F%E6%88%90%E5%A4%A7%E9%87%8F%E6%A0%B7%E6%9C%AC%EF%BC%8C%E6%97%A0%E9%9C%80%E4%BA%BA%E5%B7%A5%E5%B9%B2%E9%A2%84%EF%BC%8C%E5%B9%B6%E4%B8%94%E9%81%BF%E5%85%8D%E4%BA%86%E5%9F%9F%E5%B7%AE%E8%B7%9D%E3%80%82%E4%BD%86%E7%BD%91%E7%BB%9C%E7%94%9F%E6%88%90%E6%A0%B7%E6%9C%AC%E7%9A%84%E4%BF%9D%E7%9C%9F%E6%80%A7%E5%92%8C%E7%A8%B3%E5%AE%9A%E6%80%A7%E4%BB%8D%E9%9C%80%E8%80%83%E8%99%91%3C/span%3E" STYLE="fork"/>
      </node>
    </node>
    <node TEXT="基于迁移学习" ID="2agPQk2Vzq" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E8%BF%81%E7%A7%BB%E5%AD%A6%E4%B9%A0%3C/span%3E" STYLE="bubble" POSITION="right">
      <node TEXT="基于迁移学习主要是基于预训练微调。通过在大的数据集训练任务，获得数据知识，之后将知识作为特征或者模型参数迁移到另一个任务中。重点在学习数据知识" ID="vgSXDCYdyn" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E8%BF%81%E7%A7%BB%E5%AD%A6%E4%B9%A0%E4%B8%BB%E8%A6%81%E6%98%AF%E5%9F%BA%E4%BA%8E%E9%A2%84%E8%AE%AD%E7%BB%83%E5%BE%AE%E8%B0%83%E3%80%82%E9%80%9A%E8%BF%87%E5%9C%A8%E5%A4%A7%E7%9A%84%E6%95%B0%E6%8D%AE%E9%9B%86%E8%AE%AD%E7%BB%83%E4%BB%BB%E5%8A%A1%EF%BC%8C%E8%8E%B7%E5%BE%97%E6%95%B0%E6%8D%AE%E7%9F%A5%E8%AF%86%EF%BC%8C%E4%B9%8B%E5%90%8E%E5%B0%86%E7%9F%A5%E8%AF%86%E4%BD%9C%E4%B8%BA%E7%89%B9%E5%BE%81%E6%88%96%E8%80%85%E6%A8%A1%E5%9E%8B%E5%8F%82%E6%95%B0%E8%BF%81%E7%A7%BB%E5%88%B0%E5%8F%A6%E4%B8%80%E4%B8%AA%E4%BB%BB%E5%8A%A1%E4%B8%AD%E3%80%82%E9%87%8D%E7%82%B9%E5%9C%A8%E5%AD%A6%E4%B9%A0%E6%95%B0%E6%8D%AE%E7%9F%A5%E8%AF%86%3C/span%3E" STYLE="fork"/>
      <node TEXT="基于微调" ID="7jIrvOMngk" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E5%BE%AE%E8%B0%83%3C/span%3E" STYLE="fork">
        <node TEXT="主要思想是在大数据集上训练网络，然后在小数据集上微调网络，里面的一些学习率微调方法值得借鉴，如：使用三角学习率，学习率从零开始，逐渐增加，然后在逐渐减少；每一层的学习率也不相同，在靠近输入的层，主要提取底层特征，因此使用较小学习率，而在靠近输出的层使用较大的学习率。使得模型快速收敛。余弦相似度、自适应优化器" ID="eXFGE68xEu" _mubu_text="%3Cspan%3E%E4%B8%BB%E8%A6%81%E6%80%9D%E6%83%B3%E6%98%AF%E5%9C%A8%E5%A4%A7%E6%95%B0%E6%8D%AE%E9%9B%86%E4%B8%8A%E8%AE%AD%E7%BB%83%E7%BD%91%E7%BB%9C%EF%BC%8C%E7%84%B6%E5%90%8E%E5%9C%A8%E5%B0%8F%E6%95%B0%E6%8D%AE%E9%9B%86%E4%B8%8A%E5%BE%AE%E8%B0%83%E7%BD%91%E7%BB%9C%EF%BC%8C%E9%87%8C%E9%9D%A2%E7%9A%84%E4%B8%80%E4%BA%9B%E5%AD%A6%E4%B9%A0%E7%8E%87%E5%BE%AE%E8%B0%83%E6%96%B9%E6%B3%95%E5%80%BC%E5%BE%97%E5%80%9F%E9%89%B4%EF%BC%8C%E5%A6%82%EF%BC%9A%E4%BD%BF%E7%94%A8%E4%B8%89%E8%A7%92%E5%AD%A6%E4%B9%A0%E7%8E%87%EF%BC%8C%E5%AD%A6%E4%B9%A0%E7%8E%87%E4%BB%8E%E9%9B%B6%E5%BC%80%E5%A7%8B%EF%BC%8C%E9%80%90%E6%B8%90%E5%A2%9E%E5%8A%A0%EF%BC%8C%E7%84%B6%E5%90%8E%E5%9C%A8%E9%80%90%E6%B8%90%E5%87%8F%E5%B0%91%EF%BC%9B%E6%AF%8F%E4%B8%80%E5%B1%82%E7%9A%84%E5%AD%A6%E4%B9%A0%E7%8E%87%E4%B9%9F%E4%B8%8D%E7%9B%B8%E5%90%8C%EF%BC%8C%E5%9C%A8%E9%9D%A0%E8%BF%91%E8%BE%93%E5%85%A5%E7%9A%84%E5%B1%82%EF%BC%8C%E4%B8%BB%E8%A6%81%E6%8F%90%E5%8F%96%E5%BA%95%E5%B1%82%E7%89%B9%E5%BE%81%EF%BC%8C%E5%9B%A0%E6%AD%A4%E4%BD%BF%E7%94%A8%E8%BE%83%E5%B0%8F%E5%AD%A6%E4%B9%A0%E7%8E%87%EF%BC%8C%E8%80%8C%E5%9C%A8%E9%9D%A0%E8%BF%91%E8%BE%93%E5%87%BA%E7%9A%84%E5%B1%82%E4%BD%BF%E7%94%A8%E8%BE%83%E5%A4%A7%E7%9A%84%E5%AD%A6%E4%B9%A0%E7%8E%87%E3%80%82%E4%BD%BF%E5%BE%97%E6%A8%A1%E5%9E%8B%E5%BF%AB%E9%80%9F%E6%94%B6%E6%95%9B%E3%80%82%E4%BD%99%E5%BC%A6%E7%9B%B8%E4%BC%BC%E5%BA%A6%E3%80%81%E8%87%AA%E9%80%82%E5%BA%94%E4%BC%98%E5%8C%96%E5%99%A8%3C/span%3E" STYLE="fork"/>
      </node>
      <node TEXT="基于迁移学习的方法往往使得网络能够快速适应新任务，缺点在于通常要求大数据集与小数据集具有相关性，即他们的分布大致相同。" ID="US6sBWInEy" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E8%BF%81%E7%A7%BB%E5%AD%A6%E4%B9%A0%E7%9A%84%E6%96%B9%E6%B3%95%E5%BE%80%E5%BE%80%E4%BD%BF%E5%BE%97%E7%BD%91%E7%BB%9C%E8%83%BD%E5%A4%9F%E5%BF%AB%E9%80%9F%E9%80%82%E5%BA%94%E6%96%B0%E4%BB%BB%E5%8A%A1%EF%BC%8C%E7%BC%BA%E7%82%B9%E5%9C%A8%E4%BA%8E%E9%80%9A%E5%B8%B8%E8%A6%81%E6%B1%82%E5%A4%A7%E6%95%B0%E6%8D%AE%E9%9B%86%E4%B8%8E%E5%B0%8F%E6%95%B0%E6%8D%AE%E9%9B%86%E5%85%B7%E6%9C%89%E7%9B%B8%E5%85%B3%E6%80%A7%EF%BC%8C%E5%8D%B3%E4%BB%96%E4%BB%AC%E7%9A%84%E5%88%86%E5%B8%83%E5%A4%A7%E8%87%B4%E7%9B%B8%E5%90%8C%E3%80%82%3C/span%3E" STYLE="fork"/>
    </node>
    <node TEXT="基于元学习" ID="UgLNHlKzDq" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E5%85%83%E5%AD%A6%E4%B9%A0%3C/span%3E" STYLE="bubble" POSITION="left">
      <node TEXT="基于元学习的方法可以分类基于度量学习、学习模型参数和学习转移信息（图神经网络）" ID="UxEP5V8TiD" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E5%85%83%E5%AD%A6%E4%B9%A0%E7%9A%84%E6%96%B9%E6%B3%95%E5%8F%AF%E4%BB%A5%E5%88%86%E7%B1%BB%E5%9F%BA%E4%BA%8E%E5%BA%A6%E9%87%8F%E5%AD%A6%E4%B9%A0%E3%80%81%E5%AD%A6%E4%B9%A0%E6%A8%A1%E5%9E%8B%E5%8F%82%E6%95%B0%E5%92%8C%E5%AD%A6%E4%B9%A0%E8%BD%AC%E7%A7%BB%E4%BF%A1%E6%81%AF%EF%BC%88%E5%9B%BE%E7%A5%9E%E7%BB%8F%E7%BD%91%E7%BB%9C%EF%BC%89%3C/span%3E" STYLE="fork">
        <node TEXT="元学习的主要是学习数据和任务。元网络主要由两部分：多个base-learner和meta-learner，其中base-learner负责在单一任务的快速学习，meta-learner负责在抽象的元空间学习，逐渐积累常识。当新任务来的时候base-learner将元信息传给m，m进行处理，从而给b和m分配参数。就像是领导和员工，员工整理任务然后上报领导，领导定方向，然后员工去干活。其中在学习不同任务的泛化性时，参数更新缓慢，在适应新任务时，参数更新迅速。" ID="MfU4cf4k6x" _mubu_text="%3Cspan%3E%E5%85%83%E5%AD%A6%E4%B9%A0%E7%9A%84%E4%B8%BB%E8%A6%81%E6%98%AF%E5%AD%A6%E4%B9%A0%E6%95%B0%E6%8D%AE%E5%92%8C%E4%BB%BB%E5%8A%A1%E3%80%82%E5%85%83%E7%BD%91%E7%BB%9C%E4%B8%BB%E8%A6%81%E7%94%B1%E4%B8%A4%E9%83%A8%E5%88%86%EF%BC%9A%E5%A4%9A%E4%B8%AAbase-learner%E5%92%8Cmeta-learner%EF%BC%8C%E5%85%B6%E4%B8%ADbase-learner%E8%B4%9F%E8%B4%A3%E5%9C%A8%E5%8D%95%E4%B8%80%E4%BB%BB%E5%8A%A1%E7%9A%84%E5%BF%AB%E9%80%9F%E5%AD%A6%E4%B9%A0%EF%BC%8Cmeta-learner%E8%B4%9F%E8%B4%A3%E5%9C%A8%E6%8A%BD%E8%B1%A1%E7%9A%84%E5%85%83%E7%A9%BA%E9%97%B4%E5%AD%A6%E4%B9%A0%EF%BC%8C%E9%80%90%E6%B8%90%E7%A7%AF%E7%B4%AF%E5%B8%B8%E8%AF%86%E3%80%82%E5%BD%93%E6%96%B0%E4%BB%BB%E5%8A%A1%E6%9D%A5%E7%9A%84%E6%97%B6%E5%80%99base-learner%E5%B0%86%E5%85%83%E4%BF%A1%E6%81%AF%E4%BC%A0%E7%BB%99m%EF%BC%8Cm%E8%BF%9B%E8%A1%8C%E5%A4%84%E7%90%86%EF%BC%8C%E4%BB%8E%E8%80%8C%E7%BB%99b%E5%92%8Cm%E5%88%86%E9%85%8D%E5%8F%82%E6%95%B0%E3%80%82%E5%B0%B1%E5%83%8F%E6%98%AF%E9%A2%86%E5%AF%BC%E5%92%8C%E5%91%98%E5%B7%A5%EF%BC%8C%E5%91%98%E5%B7%A5%E6%95%B4%E7%90%86%E4%BB%BB%E5%8A%A1%E7%84%B6%E5%90%8E%E4%B8%8A%E6%8A%A5%E9%A2%86%E5%AF%BC%EF%BC%8C%E9%A2%86%E5%AF%BC%E5%AE%9A%E6%96%B9%E5%90%91%EF%BC%8C%E7%84%B6%E5%90%8E%E5%91%98%E5%B7%A5%E5%8E%BB%E5%B9%B2%E6%B4%BB%E3%80%82%E5%85%B6%E4%B8%AD%E5%9C%A8%E5%AD%A6%E4%B9%A0%E4%B8%8D%E5%90%8C%E4%BB%BB%E5%8A%A1%E7%9A%84%E6%B3%9B%E5%8C%96%E6%80%A7%E6%97%B6%EF%BC%8C%E5%8F%82%E6%95%B0%E6%9B%B4%E6%96%B0%E7%BC%93%E6%85%A2%EF%BC%8C%E5%9C%A8%E9%80%82%E5%BA%94%E6%96%B0%E4%BB%BB%E5%8A%A1%E6%97%B6%EF%BC%8C%E5%8F%82%E6%95%B0%E6%9B%B4%E6%96%B0%E8%BF%85%E9%80%9F%E3%80%82%3C/span%3E" STYLE="fork"/>
        <node TEXT="元学习的基本学习单元是由若干个学习任务组成的，每个任务的训练数据、验证数据和测试数据都是随机分配的。" ID="ClW9OWfhC3" _mubu_text="%3Cspan%3E%E5%85%83%E5%AD%A6%E4%B9%A0%E7%9A%84%E5%9F%BA%E6%9C%AC%E5%AD%A6%E4%B9%A0%E5%8D%95%E5%85%83%E6%98%AF%E7%94%B1%E8%8B%A5%E5%B9%B2%E4%B8%AA%E5%AD%A6%E4%B9%A0%E4%BB%BB%E5%8A%A1%E7%BB%84%E6%88%90%E7%9A%84%EF%BC%8C%E6%AF%8F%E4%B8%AA%E4%BB%BB%E5%8A%A1%E7%9A%84%E8%AE%AD%E7%BB%83%E6%95%B0%E6%8D%AE%E3%80%81%E9%AA%8C%E8%AF%81%E6%95%B0%E6%8D%AE%E5%92%8C%E6%B5%8B%E8%AF%95%E6%95%B0%E6%8D%AE%E9%83%BD%E6%98%AF%E9%9A%8F%E6%9C%BA%E5%88%86%E9%85%8D%E7%9A%84%E3%80%82%3C/span%3E" STYLE="fork"/>
        <node TEXT="与迁移学习对比，该方法能够快速适应新任务，并且能够与分类、回归与强化等模型结合。当该方法需要一个大的辅助数据集去学习泛化性，在遥感领域中很难实现。" ID="JJXFoRZj9E" _mubu_text="%3Cspan%3E%E4%B8%8E%E8%BF%81%E7%A7%BB%E5%AD%A6%E4%B9%A0%E5%AF%B9%E6%AF%94%EF%BC%8C%E8%AF%A5%E6%96%B9%E6%B3%95%E8%83%BD%E5%A4%9F%E5%BF%AB%E9%80%9F%E9%80%82%E5%BA%94%E6%96%B0%E4%BB%BB%E5%8A%A1%EF%BC%8C%E5%B9%B6%E4%B8%94%E8%83%BD%E5%A4%9F%E4%B8%8E%E5%88%86%E7%B1%BB%E3%80%81%E5%9B%9E%E5%BD%92%E4%B8%8E%E5%BC%BA%E5%8C%96%E7%AD%89%E6%A8%A1%E5%9E%8B%E7%BB%93%E5%90%88%E3%80%82%E5%BD%93%E8%AF%A5%E6%96%B9%E6%B3%95%E9%9C%80%E8%A6%81%E4%B8%80%E4%B8%AA%E5%A4%A7%E7%9A%84%E8%BE%85%E5%8A%A9%E6%95%B0%E6%8D%AE%E9%9B%86%E5%8E%BB%E5%AD%A6%E4%B9%A0%E6%B3%9B%E5%8C%96%E6%80%A7%EF%BC%8C%E5%9C%A8%E9%81%A5%E6%84%9F%E9%A2%86%E5%9F%9F%E4%B8%AD%E5%BE%88%E9%9A%BE%E5%AE%9E%E7%8E%B0%E3%80%82%3C/span%3E" STYLE="fork"/>
      </node>
      <node TEXT="基于图神经网络：" ID="AdAYaDm1Id" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E5%9B%BE%E7%A5%9E%E7%BB%8F%E7%BD%91%E7%BB%9C%EF%BC%9A%3C/span%3E" STYLE="fork">
        <node TEXT="将输入的样本用图来表示，样本作为点，点与点之间用边连接，有根据点去分类的，也有根据边去分类。在介绍点分类时，有将输入向量与标签向量进行叠加，所以感觉标签向量（多模态融合）的连接上也可以考虑创新。" ID="OA2UDI9fEy" _mubu_text="%3Cspan%3E%E5%B0%86%E8%BE%93%E5%85%A5%E7%9A%84%E6%A0%B7%E6%9C%AC%E7%94%A8%E5%9B%BE%E6%9D%A5%E8%A1%A8%E7%A4%BA%EF%BC%8C%E6%A0%B7%E6%9C%AC%E4%BD%9C%E4%B8%BA%E7%82%B9%EF%BC%8C%E7%82%B9%E4%B8%8E%E7%82%B9%E4%B9%8B%E9%97%B4%E7%94%A8%E8%BE%B9%E8%BF%9E%E6%8E%A5%EF%BC%8C%E6%9C%89%E6%A0%B9%E6%8D%AE%E7%82%B9%E5%8E%BB%E5%88%86%E7%B1%BB%E7%9A%84%EF%BC%8C%E4%B9%9F%E6%9C%89%E6%A0%B9%E6%8D%AE%E8%BE%B9%E5%8E%BB%E5%88%86%E7%B1%BB%E3%80%82%E5%9C%A8%E4%BB%8B%E7%BB%8D%E7%82%B9%E5%88%86%E7%B1%BB%E6%97%B6%EF%BC%8C%E6%9C%89%E5%B0%86%E8%BE%93%E5%85%A5%E5%90%91%E9%87%8F%E4%B8%8E%E6%A0%87%E7%AD%BE%E5%90%91%E9%87%8F%E8%BF%9B%E8%A1%8C%E5%8F%A0%E5%8A%A0%EF%BC%8C%E6%89%80%E4%BB%A5%E6%84%9F%E8%A7%89%E6%A0%87%E7%AD%BE%E5%90%91%E9%87%8F%EF%BC%88%E5%A4%9A%E6%A8%A1%E6%80%81%E8%9E%8D%E5%90%88%EF%BC%89%E7%9A%84%E8%BF%9E%E6%8E%A5%E4%B8%8A%E4%B9%9F%E5%8F%AF%E4%BB%A5%E8%80%83%E8%99%91%E5%88%9B%E6%96%B0%E3%80%82%3C/span%3E" STYLE="fork"/>
      </node>
    </node>
    <node TEXT="基于度量学习：" ID="3SZdTsqAzY" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E5%BA%A6%E9%87%8F%E5%AD%A6%E4%B9%A0%EF%BC%9A%3C/span%3E" STYLE="bubble" POSITION="left">
      <node TEXT="本质是找到一个合适的特征空间，能够使得相似样本紧凑，不相似样本稀疏。在临近分类中如何计算两个样本间的距离也是度量学习的改进方向。" ID="TYuHTBlXUz" _mubu_text="%3Cspan%3E%E6%9C%AC%E8%B4%A8%E6%98%AF%E6%89%BE%E5%88%B0%E4%B8%80%E4%B8%AA%E5%90%88%E9%80%82%E7%9A%84%E7%89%B9%E5%BE%81%E7%A9%BA%E9%97%B4%EF%BC%8C%E8%83%BD%E5%A4%9F%E4%BD%BF%E5%BE%97%E7%9B%B8%E4%BC%BC%E6%A0%B7%E6%9C%AC%E7%B4%A7%E5%87%91%EF%BC%8C%E4%B8%8D%E7%9B%B8%E4%BC%BC%E6%A0%B7%E6%9C%AC%E7%A8%80%E7%96%8F%E3%80%82%E5%9C%A8%E4%B8%B4%E8%BF%91%E5%88%86%E7%B1%BB%E4%B8%AD%E5%A6%82%E4%BD%95%E8%AE%A1%E7%AE%97%E4%B8%A4%E4%B8%AA%E6%A0%B7%E6%9C%AC%E9%97%B4%E7%9A%84%E8%B7%9D%E7%A6%BB%E4%B9%9F%E6%98%AF%E5%BA%A6%E9%87%8F%E5%AD%A6%E4%B9%A0%E7%9A%84%E6%94%B9%E8%BF%9B%E6%96%B9%E5%90%91%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="该方法使用简单，理论上能够适应各种学习任务，在小样本分类中表现优异。然而当源数据与目标数据差距太大时，泛化性能较差。" ID="kRzFJNJ9HC" _mubu_text="%3Cspan%3E%E8%AF%A5%E6%96%B9%E6%B3%95%E4%BD%BF%E7%94%A8%E7%AE%80%E5%8D%95%EF%BC%8C%E7%90%86%E8%AE%BA%E4%B8%8A%E8%83%BD%E5%A4%9F%E9%80%82%E5%BA%94%E5%90%84%E7%A7%8D%E5%AD%A6%E4%B9%A0%E4%BB%BB%E5%8A%A1%EF%BC%8C%E5%9C%A8%E5%B0%8F%E6%A0%B7%E6%9C%AC%E5%88%86%E7%B1%BB%E4%B8%AD%E8%A1%A8%E7%8E%B0%E4%BC%98%E5%BC%82%E3%80%82%E7%84%B6%E8%80%8C%E5%BD%93%E6%BA%90%E6%95%B0%E6%8D%AE%E4%B8%8E%E7%9B%AE%E6%A0%87%E6%95%B0%E6%8D%AE%E5%B7%AE%E8%B7%9D%E5%A4%AA%E5%A4%A7%E6%97%B6%EF%BC%8C%E6%B3%9B%E5%8C%96%E6%80%A7%E8%83%BD%E8%BE%83%E5%B7%AE%E3%80%82%3C/span%3E" STYLE="fork"/>
    </node>
    <node TEXT="基于多模态学习" ID="EmDTnlzfBj" _mubu_text="%3Cspan%3E%E5%9F%BA%E4%BA%8E%E5%A4%9A%E6%A8%A1%E6%80%81%E5%AD%A6%E4%B9%A0%3C/span%3E" STYLE="bubble" POSITION="left">
      <node TEXT="利用图像和文本两种模态、基于图片生成文本信息。" ID="v5wEOfMCTA" _mubu_text="%3Cspan%3E%E5%88%A9%E7%94%A8%E5%9B%BE%E5%83%8F%E5%92%8C%E6%96%87%E6%9C%AC%E4%B8%A4%E7%A7%8D%E6%A8%A1%E6%80%81%E3%80%81%E5%9F%BA%E4%BA%8E%E5%9B%BE%E7%89%87%E7%94%9F%E6%88%90%E6%96%87%E6%9C%AC%E4%BF%A1%E6%81%AF%E3%80%82%3C/span%3E" STYLE="fork"/>
    </node>
  </node>
