    <node TEXT="卷积（convolution）" ID="t5h3BkfLGN" _mubu_text="%3Cspan%3E%E5%8D%B7%E7%A7%AF%EF%BC%88convolution%EF%BC%89%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="常用互相关函数（cross-correlation），s(i,j)=I*W,对应位相乘并相加，不需要进行核翻转。" ID="sKDVUgr3Ax" _mubu_text="%3Cspan%3E%E5%B8%B8%E7%94%A8%E4%BA%92%E7%9B%B8%E5%85%B3%E5%87%BD%E6%95%B0%EF%BC%88cross-correlation%EF%BC%89%EF%BC%8Cs(i,j)=I*W,%E5%AF%B9%E5%BA%94%E4%BD%8D%E7%9B%B8%E4%B9%98%E5%B9%B6%E7%9B%B8%E5%8A%A0%EF%BC%8C%E4%B8%8D%E9%9C%80%E8%A6%81%E8%BF%9B%E8%A1%8C%E6%A0%B8%E7%BF%BB%E8%BD%AC%E3%80%82%3C/span%3E" STYLE="fork"/>
