    <node TEXT="special field" ID="ReRQSMaHcE" _mubu_text="%3Cspan%3Especial%20field%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="自定义设置，可大可小，可正可长，可重叠，可对应多个参数（fitter），减小network弹性，不足补零。" ID="POXoVlCIYB" _mubu_text="%3Cspan%3E%E8%87%AA%E5%AE%9A%E4%B9%89%E8%AE%BE%E7%BD%AE%EF%BC%8C%E5%8F%AF%E5%A4%A7%E5%8F%AF%E5%B0%8F%EF%BC%8C%E5%8F%AF%E6%AD%A3%E5%8F%AF%E9%95%BF%EF%BC%8C%E5%8F%AF%E9%87%8D%E5%8F%A0%EF%BC%8C%E5%8F%AF%E5%AF%B9%E5%BA%94%E5%A4%9A%E4%B8%AA%E5%8F%82%E6%95%B0%EF%BC%88fitter%EF%BC%89%EF%BC%8C%E5%87%8F%E5%B0%8Fnetwork%E5%BC%B9%E6%80%A7%EF%BC%8C%E4%B8%8D%E8%B6%B3%E8%A1%A5%E9%9B%B6%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="常用设置：3X3大小，对应一组fitter" ID="04mTsiySI3" _mubu_text="%3Cspan%3E%E5%B8%B8%E7%94%A8%E8%AE%BE%E7%BD%AE%EF%BC%9A3X3%E5%A4%A7%E5%B0%8F%EF%BC%8C%E5%AF%B9%E5%BA%94%E4%B8%80%E7%BB%84fitter%3C/span%3E" STYLE="fork"/>
      <node TEXT="弹性小：参数少，适合小数据集；弹性大：参数大，容易过拟合（over fitting）" ID="AjTzDMSqdQ" _mubu_text="%3Cspan%3E%E5%BC%B9%E6%80%A7%E5%B0%8F%EF%BC%9A%E5%8F%82%E6%95%B0%E5%B0%91%EF%BC%8C%E9%80%82%E5%90%88%E5%B0%8F%E6%95%B0%E6%8D%AE%E9%9B%86%EF%BC%9B%E5%BC%B9%E6%80%A7%E5%A4%A7%EF%BC%9A%E5%8F%82%E6%95%B0%E5%A4%A7%EF%BC%8C%E5%AE%B9%E6%98%93%E8%BF%87%E6%8B%9F%E5%90%88%EF%BC%88over%20fitting%EF%BC%89%3C/span%3E" STYLE="fork"/>
