    <node TEXT="参数共享（parameter sharing）" ID="UB2mXkifLK" _mubu_text="%3Cspan%3E%E5%8F%82%E6%95%B0%E5%85%B1%E4%BA%AB%EF%BC%88parameter%20sharing%EF%BC%89%3C/span%3E" STYLE="bubble" POSITION="default">
