    <node TEXT="池化（pooling）" ID="boGIq3x236" _mubu_text="%3Cspan%3E%E6%B1%A0%E5%8C%96%EF%BC%88pooling%EF%BC%89%3C/span%3E" STYLE="bubble" POSITION="default">
