    </node>
    <node TEXT="Dropout层" ID="iePavSxrYp" _mubu_text="%3Cspan%3EDropout%E5%B1%82%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="避免过拟合，在每一批次中随机拿掉部分神经元进行训练，第二次选中的神经元则继续更新参数，第二次未选中但第一次已经更新的，保留参数。" ID="9jbWRizO9t" _mubu_text="%3Cspan%3E%E9%81%BF%E5%85%8D%E8%BF%87%E6%8B%9F%E5%90%88%EF%BC%8C%E5%9C%A8%E6%AF%8F%E4%B8%80%E6%89%B9%E6%AC%A1%E4%B8%AD%E9%9A%8F%E6%9C%BA%E6%8B%BF%E6%8E%89%E9%83%A8%E5%88%86%E7%A5%9E%E7%BB%8F%E5%85%83%E8%BF%9B%E8%A1%8C%E8%AE%AD%E7%BB%83%EF%BC%8C%E7%AC%AC%E4%BA%8C%E6%AC%A1%E9%80%89%E4%B8%AD%E7%9A%84%E7%A5%9E%E7%BB%8F%E5%85%83%E5%88%99%E7%BB%A7%E7%BB%AD%E6%9B%B4%E6%96%B0%E5%8F%82%E6%95%B0%EF%BC%8C%E7%AC%AC%E4%BA%8C%E6%AC%A1%E6%9C%AA%E9%80%89%E4%B8%AD%E4%BD%86%E7%AC%AC%E4%B8%80%E6%AC%A1%E5%B7%B2%E7%BB%8F%E6%9B%B4%E6%96%B0%E7%9A%84%EF%BC%8C%E4%BF%9D%E7%95%99%E5%8F%82%E6%95%B0%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="常用于神经元数目多的层" ID="vXo0qOPfGb" _mubu_text="%3Cspan%3E%E5%B8%B8%E7%94%A8%E4%BA%8E%E7%A5%9E%E7%BB%8F%E5%85%83%E6%95%B0%E7%9B%AE%E5%A4%9A%E7%9A%84%E5%B1%82%3C/span%3E" STYLE="fork"/>
