    <node TEXT="在网络中常常使用convolution和pooling组合" ID="qwmj4Nbj18" _mubu_text="%3Cspan%3E%E5%9C%A8%E7%BD%91%E7%BB%9C%E4%B8%AD%E5%B8%B8%E5%B8%B8%E4%BD%BF%E7%94%A8convolution%E5%92%8Cpooling%E7%BB%84%E5%90%88%3C/span%3E" STYLE="bubble" POSITION="default"/>
    <node TEXT="模型深度、参数对结果的影响" ID="UA7Ra9fTtK" _mubu_text="%3Cspan%3E%E6%A8%A1%E5%9E%8B%E6%B7%B1%E5%BA%A6%E3%80%81%E5%8F%82%E6%95%B0%E5%AF%B9%E7%BB%93%E6%9E%9C%E7%9A%84%E5%BD%B1%E5%93%8D%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="单层的前馈网络可以表示任意函数，但网络层会很大，使用深的模型能减少所需的单元，并且更容易学习和泛化。" ID="UyAYqHmAqV" _mubu_text="%3Cspan%3E%E5%8D%95%E5%B1%82%E7%9A%84%E5%89%8D%E9%A6%88%E7%BD%91%E7%BB%9C%E5%8F%AF%E4%BB%A5%E8%A1%A8%E7%A4%BA%E4%BB%BB%E6%84%8F%E5%87%BD%E6%95%B0%EF%BC%8C%E4%BD%86%E7%BD%91%E7%BB%9C%E5%B1%82%E4%BC%9A%E5%BE%88%E5%A4%A7%EF%BC%8C%E4%BD%BF%E7%94%A8%E6%B7%B1%E7%9A%84%E6%A8%A1%E5%9E%8B%E8%83%BD%E5%87%8F%E5%B0%91%E6%89%80%E9%9C%80%E7%9A%84%E5%8D%95%E5%85%83%EF%BC%8C%E5%B9%B6%E4%B8%94%E6%9B%B4%E5%AE%B9%E6%98%93%E5%AD%A6%E4%B9%A0%E5%92%8C%E6%B3%9B%E5%8C%96%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="浅层模型在参数过多时容易过拟合（over fitting），深层模型参数更多时效果更好。" ID="yHYde0ACqn" _mubu_text="%3Cspan%3E%E6%B5%85%E5%B1%82%E6%A8%A1%E5%9E%8B%E5%9C%A8%E5%8F%82%E6%95%B0%E8%BF%87%E5%A4%9A%E6%97%B6%E5%AE%B9%E6%98%93%E8%BF%87%E6%8B%9F%E5%90%88%EF%BC%88over%20fitting%EF%BC%89%EF%BC%8C%E6%B7%B1%E5%B1%82%E6%A8%A1%E5%9E%8B%E5%8F%82%E6%95%B0%E6%9B%B4%E5%A4%9A%E6%97%B6%E6%95%88%E6%9E%9C%E6%9B%B4%E5%A5%BD%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="减少过拟合的方法：减少参数、共用参数" ID="38f5u9Efzk" _mubu_text="%3Cspan%3E%E5%87%8F%E5%B0%91%E8%BF%87%E6%8B%9F%E5%90%88%E7%9A%84%E6%96%B9%E6%B3%95%EF%BC%9A%E5%87%8F%E5%B0%91%E5%8F%82%E6%95%B0%E3%80%81%E5%85%B1%E7%94%A8%E5%8F%82%E6%95%B0%3C/span%3E" STYLE="fork"/>
      <node TEXT="在深度学习中，可以实现小的模型取得好的结果。" ID="B9Z9HCVtnT" _mubu_text="%3Cspan%3E%E5%9C%A8%E6%B7%B1%E5%BA%A6%E5%AD%A6%E4%B9%A0%E4%B8%AD%EF%BC%8C%E5%8F%AF%E4%BB%A5%E5%AE%9E%E7%8E%B0%E5%B0%8F%E7%9A%84%E6%A8%A1%E5%9E%8B%E5%8F%96%E5%BE%97%E5%A5%BD%E7%9A%84%E7%BB%93%E6%9E%9C%E3%80%82%3C/span%3E" STYLE="fork"/>
